mapper.select:
select * from sys_menu

     and id = #{id}
     and pid = #{pid}
     and type = #{type}
     and title = #{title}
     and icon = #{icon}
     and path = #{path}
     and target = #{target}
     and permission = #{permission}
     and sub_count = #{sub_count}
     and show = #{show}
     and sort = #{sort}
     and create_time = #{create_time}
     and create_by = #{create_by}
     and update_time = #{update_time}
     and update_by = #{update_by}


ORDER BY PID ASC NULLS FIRST ,SORT ASC

select * from sys_menu

     and id = #{id}
     and pid = #{pid}
     and type = #{type}
     and title = #{title}
     and icon = #{icon}
     and path = #{path}
     and target = #{target}
     and permission = #{permission}
     and sub_count = #{sub_count}
     and show = #{show}
     and sort = #{sort}
     and create_time = #{create_time}
     and create_by = #{create_by}
     and update_time = #{update_time}
     and update_by = #{update_by}



ORDER BY PID ASC, IF(ISNULL(PID),-1,1), SORT ASC

mapper.update:
update sys_menu
pid=#{pid}, type=#{type}, title=#{title}, icon=#{icon}, path=#{path}, target=#{target}, permission=#{permission}, sub_count=#{sub_count}, show=#{show}, sort=#{sort}, update_time=#{update_time}, update_by=#{update_by},
where id=#{id}
