mapper.select:
SELECT * FROM SYS_ROLE

     and id = #{id}
     and name like #{name}
     and description like #{description}
     and status = #{status}
     and create_by = #{create_by}
     and create_time = #{create_time}
     and update_by = #{update_by}
     and update_time = #{update_time}

ORDER BY ID DESC

mapper.update:
update sys_role
name=#{name}, description=#{description}, status=#{status}, create_by=#{create_by}, create_time=#{create_time}, update_by=#{update_by}, update_time=#{update_time},
where id=#{id}

mapper.select:
select r.* from sys_role as r inner join (
select r.role_id,u.user_name from sys_user as u inner join sys_role_user  as r on u.id = r.user_id
where u.id = #{user_id}
)as t1 on r.id=t1.role_id
