mapper.select:
select * from sys_user

     and id = cast(#{id} as numeric) 
      and nick_name like #{nick_name}  
      and user_name like #{user_name} 
      and email like #{email} 
     and status = #{status}

select * from sys_user

      and user_name = #{user_name} 
      and email = #{email} 

limit 1
